func GetCityFromCode(code string) string {
	//六位城市编码
	//根据前四位编码返回城市名
	if len(code) == 6 {
		if _, ok := CODE2NAME[code[:4]]; ok {
			return CODE2NAME[code[:4]]
		} else {
			if _, ok := MDCG_CITY[code[:2]]; ok {
				return MDCG_CITY[code[:2]]
			} else if _, ok := OTHER_CITY[code]; ok {
				return OTHER_CITY[code]
			}

		}
	}
	return ""
}

